/************************************************************
 *
 * Initialize the saHpiResourceTable table by defining its contents and how it's structured
 */
void
initialize_table_saHpiResourceTable(void)
{
    netsnmp_table_registration_info *table_info;

    if(my_handler) {
        snmp_log(LOG_ERR, "initialize_table_saHpiResourceTable_handler called again\n");
        return;
    }

    memset(&cb, 0x00, sizeof(cb));

    /** create the table structure itself */
    table_info = SNMP_MALLOC_TYPEDEF(netsnmp_table_registration_info);

    /* if your table is read only, it's easiest to change the
       HANDLER_CAN_RWRITE definition below to HANDLER_CAN_RONLY */
    my_handler = netsnmp_create_handler_registration("saHpiResourceTable",
                                             netsnmp_table_array_helper_handler,
                                             saHpiResourceTable_oid,
                                             saHpiResourceTable_oid_len,
                                             HANDLER_CAN_RWRITE);
            
    if (!my_handler || !table_info) {
        snmp_log(LOG_ERR, "malloc failed in "
                 "initialize_table_saHpiResourceTable_handler\n");
        return; /** mallocs failed */
    }

    /***************************************************
     * Setting up the table's definition
     */
    /*
     * TODO: add any external indexes here.
     */
        /** TODO: add code for external index(s)! */

    /*
     * internal indexes
     */
        /** index: saHpiDomainId */
        netsnmp_table_helper_add_index(table_info, ASN_UNSIGNED);
        /** index: saHpiResourceId */
        netsnmp_table_helper_add_index(table_info, ASN_UNSIGNED);
        /** index: saHpiEntryId */
        netsnmp_table_helper_add_index(table_info, ASN_UNSIGNED);

    table_info->min_column = saHpiResourceTable_COL_MIN;
    table_info->max_column = saHpiResourceTable_COL_MAX;

    /***************************************************
     * registering the table with the master agent
     */
    cb.get_value = saHpiResourceTable_get_value;
    cb.container = netsnmp_container_find("saHpiResourceTable_primary:"
                                          "saHpiResourceTable:"
                                          "table_container");
#ifdef saHpiResourceTable_IDX2
    netsnmp_container_add_index(cb.container,
                                netsnmp_container_find("saHpiResourceTable_secondary:"
                                                       "saHpiResourceTable:"
                                                       "table_container"));
    cb.container->next->compare = saHpiResourceTable_cmp;
#endif
#ifdef saHpiResourceTable_SET_HANDLING
    cb.can_set = 1;
#ifdef saHpiResourceTable_ROW_CREATION
    cb.create_row = (UserRowMethod*)saHpiResourceTable_create_row;
#endif
    cb.duplicate_row = (UserRowMethod*)saHpiResourceTable_duplicate_row;
    cb.delete_row = (UserRowMethod*)saHpiResourceTable_delete_row;
    cb.row_copy = (Netsnmp_User_Row_Operation *)saHpiResourceTable_row_copy;

    cb.can_activate = (Netsnmp_User_Row_Action *)saHpiResourceTable_can_activate;
    cb.can_deactivate = (Netsnmp_User_Row_Action *)saHpiResourceTable_can_deactivate;
    cb.can_delete = (Netsnmp_User_Row_Action *)saHpiResourceTable_can_delete;

    cb.set_reserve1 = saHpiResourceTable_set_reserve1;
    cb.set_reserve2 = saHpiResourceTable_set_reserve2;
    cb.set_action = saHpiResourceTable_set_action;
    cb.set_commit = saHpiResourceTable_set_commit;
    cb.set_free = saHpiResourceTable_set_free;
    cb.set_undo = saHpiResourceTable_set_undo;
#endif
    DEBUGMSGTL(("initialize_table_saHpiResourceTable",
                "Registering table saHpiResourceTable "
                "as a table array\n"));
    netsnmp_table_container_register(my_handler, table_info, &cb,
                                     cb.container, 1);
}

/************************************************************
 * saHpiResourceTable_get_value
 *
 * This routine is called for get requests to copy the data
 * from the context to the varbind for the request. If the
 * context has been properly maintained, you don't need to
 * change in code in this fuction.
 */
int saHpiResourceTable_get_value(
            netsnmp_request_info *request,
            netsnmp_index *item,
            netsnmp_table_request_info *table_info )
{
    netsnmp_variable_list *var = request->requestvb;
    saHpiResourceTable_context *context = (saHpiResourceTable_context *)item;

    switch(table_info->colnum) {

        case COLUMN_SAHPIRESOURCEID:
            /** UNSIGNED32 = ASN_UNSIGNED */
            snmp_set_var_typed_value(var, ASN_UNSIGNED,
                         (char*)&context->saHpiResourceId,
                         sizeof(context->saHpiResourceId) );
        break;
    
        case COLUMN_SAHPIENTRYID:
            /** UNSIGNED32 = ASN_UNSIGNED */
            snmp_set_var_typed_value(var, ASN_UNSIGNED,
                         (char*)&context->saHpiEntryId,
                         sizeof(context->saHpiEntryId) );
        break;
    
        case COLUMN_SAHPIRESOURCEENTITYPATH:
            /** OCTETSTR = ASN_OCTET_STR */
            snmp_set_var_typed_value(var, ASN_OCTET_STR,
                         (char*)&context->saHpiResourceEntityPath,
                         context->saHpiResourceEntityPath_len );
        break;
    
        case COLUMN_SAHPIRESOURCECAPABILITIES:
            /** OCTETSTR = ASN_OCTET_STR */
            snmp_set_var_typed_value(var, ASN_OCTET_STR,
                         (char*)&context->saHpiResourceCapabilities,
                         context->saHpiResourceCapabilities_len );
        break;
    
        case COLUMN_SAHPIRESOURCEHOTSWAPCAPABILITIES:
            /** UNSIGNED32 = ASN_UNSIGNED */
            snmp_set_var_typed_value(var, ASN_UNSIGNED,
                         (char*)&context->saHpiResourceHotSwapCapabilities,
                         sizeof(context->saHpiResourceHotSwapCapabilities) );
        break;
    
        case COLUMN_SAHPIRESOURCESEVERITY:
            /** INTEGER = ASN_INTEGER */
            snmp_set_var_typed_value(var, ASN_INTEGER,
                         (char*)&context->saHpiResourceSeverity,
                         sizeof(context->saHpiResourceSeverity) );
        break;
    
        case COLUMN_SAHPIRESOURCEFAILED:
            /** TruthValue = ASN_INTEGER */
            snmp_set_var_typed_value(var, ASN_INTEGER,
                         (char*)&context->saHpiResourceFailed,
                         sizeof(context->saHpiResourceFailed) );
        break;
    
        case COLUMN_SAHPIRESOURCEINFORESOURCEREV:
            /** UNSIGNED32 = ASN_UNSIGNED */
            snmp_set_var_typed_value(var, ASN_UNSIGNED,
                         (char*)&context->saHpiResourceInfoResourceRev,
                         sizeof(context->saHpiResourceInfoResourceRev) );
        break;
    
        case COLUMN_SAHPIRESOURCEINFOSPECIFICVER:
            /** UNSIGNED32 = ASN_UNSIGNED */
            snmp_set_var_typed_value(var, ASN_UNSIGNED,
                         (char*)&context->saHpiResourceInfoSpecificVer,
                         sizeof(context->saHpiResourceInfoSpecificVer) );
        break;
    
        case COLUMN_SAHPIRESOURCEINFODEVICESUPPORT:
            /** UNSIGNED32 = ASN_UNSIGNED */
            snmp_set_var_typed_value(var, ASN_UNSIGNED,
                         (char*)&context->saHpiResourceInfoDeviceSupport,
                         sizeof(context->saHpiResourceInfoDeviceSupport) );
        break;
    
        case COLUMN_SAHPIRESOURCEINFOMANUFACTURERID:
            /** UNSIGNED32 = ASN_UNSIGNED */
            snmp_set_var_typed_value(var, ASN_UNSIGNED,
                         (char*)&context->saHpiResourceInfoManufacturerId,
                         sizeof(context->saHpiResourceInfoManufacturerId) );
        break;
    
        case COLUMN_SAHPIRESOURCEINFOPRODUCTID:
            /** UNSIGNED32 = ASN_UNSIGNED */
            snmp_set_var_typed_value(var, ASN_UNSIGNED,
                         (char*)&context->saHpiResourceInfoProductId,
                         sizeof(context->saHpiResourceInfoProductId) );
        break;
    
        case COLUMN_SAHPIRESOURCEINFOFIRMWAREMAJORREV:
            /** UNSIGNED32 = ASN_UNSIGNED */
            snmp_set_var_typed_value(var, ASN_UNSIGNED,
                         (char*)&context->saHpiResourceInfoFirmwareMajorRev,
                         sizeof(context->saHpiResourceInfoFirmwareMajorRev) );
        break;
    
        case COLUMN_SAHPIRESOURCEINFOFIRMWAREMINORREV:
            /** UNSIGNED32 = ASN_UNSIGNED */
            snmp_set_var_typed_value(var, ASN_UNSIGNED,
                         (char*)&context->saHpiResourceInfoFirmwareMinorRev,
                         sizeof(context->saHpiResourceInfoFirmwareMinorRev) );
        break;
    
        case COLUMN_SAHPIRESOURCEINFOAUXFIRMWAREREV:
            /** UNSIGNED32 = ASN_UNSIGNED */
            snmp_set_var_typed_value(var, ASN_UNSIGNED,
                         (char*)&context->saHpiResourceInfoAuxFirmwareRev,
                         sizeof(context->saHpiResourceInfoAuxFirmwareRev) );
        break;
    
        case COLUMN_SAHPIRESOURCEINFOGUID:
            /** OCTETSTR = ASN_OCTET_STR */
            snmp_set_var_typed_value(var, ASN_OCTET_STR,
                         (char*)&context->saHpiResourceInfoGuid,
                         context->saHpiResourceInfoGuid_len );
        break;
    
        case COLUMN_SAHPIRESOURCETAGTEXTTYPE:
            /** SaHpiTextType = ASN_INTEGER */
            snmp_set_var_typed_value(var, ASN_INTEGER,
                         (char*)&context->saHpiResourceTagTextType,
                         sizeof(context->saHpiResourceTagTextType) );
        break;
    
        case COLUMN_SAHPIRESOURCETAGTEXTLANGUAGE:
            /** SaHpiTextLanguage = ASN_INTEGER */
            snmp_set_var_typed_value(var, ASN_INTEGER,
                         (char*)&context->saHpiResourceTagTextLanguage,
                         sizeof(context->saHpiResourceTagTextLanguage) );
        break;
    
        case COLUMN_SAHPIRESOURCETAG:
            /** SaHpiText = ASN_OCTET_STR */
            snmp_set_var_typed_value(var, ASN_OCTET_STR,
                         (char*)&context->saHpiResourceTag,
                         context->saHpiResourceTag_len );
        break;
    
        case COLUMN_SAHPIRESOURCEPARAMCONTROL:
            /** INTEGER = ASN_INTEGER */
            snmp_set_var_typed_value(var, ASN_INTEGER,
                         (char*)&context->saHpiResourceParamControl,
                         sizeof(context->saHpiResourceParamControl) );
        break;
    
    default: /** We shouldn't get here */
        snmp_log(LOG_ERR, "unknown column in "
                 "saHpiResourceTable_get_value\n");
        return SNMP_ERR_GENERR;
    }
    return SNMP_ERR_NOERROR;
}

/************************************************************
 * saHpiResourceTable_get_by_idx
 */
const saHpiResourceTable_context *
saHpiResourceTable_get_by_idx(netsnmp_index * hdr)
{
    return (const saHpiResourceTable_context *)
        CONTAINER_FIND(cb.container, hdr );
}


//*******************************************************
//*******************************************************
// saHpiResourceTable support fucntions
//*******************************************************
//*******************************************************
int populate_rpt(void) {

	oid 		
		rpt_oids[RESOURCE_INDEX_LEN];
	netsnmp_index 
		rpt_index;
	saHpiResourceTable_context  
		*rpt_ctx;

	int rc = 0;
	SaHpiSessionIdT sid;

	SaHpiEntryIdT EntryId;
	SaHpiEntryIdT NextEntryId;
	SaHpiRptEntryT rpt_entry;
	memset(&rpt_entry, 0, sizeof(rpt_entry));

	sid = get_session_id(SAHPI_UNSPECIFIED_DOMAIN_ID);

	EntryId = SAHPI_FIRST_ENTRY;
	while (SA_OK == saHpiRptEntryGet (sid, 
					  EntryId,
					  &NextEntryId,
					  &rpt_entry) ) { 
		EntryId = NextEntryId;
			
		/* create resource tuple/ index */
		/* saHpiDomainId, saHpiResourceId ,saHpiEntryId */

		rpt_oids[0] = SAHPI_UNSPECIFIED_DOMAIN_ID;
		rpt_oids[1] = rpt_entry.ResourceId;
		rpt_oids[2] = rpt_entry.EntryId;

		rpt_index.len =  RESOURCE_INDEX_LEN;
		rpt_index.oids = (oid *) & rpt_oids;  

		/* See if it exists. */
		rpt_ctx = NULL;
		rpt_ctx = CONTAINER_FIND (
			cb.container, 
			&rpt_index);
		// If we don't find it - we create it.

		if (!rpt_ctx) 
			/* New entry. Add it */
			rpt_ctx = saHpiResourceTable_create_row(&rpt_index);

		/* fill in values in ctx*/
		/* saHpiResourceId */
		rpt_ctx->saHpiResourceId = rpt_entry.ResourceId;

		/* saHpiEntryId */
		rpt_ctx->saHpiEntryId = rpt_entry.EntryId;

		/* saHpiResourceEntityPath */
		rpt_ctx->saHpiResourceEntityPath_len = entitypath2string(
					&rpt_entry.ResourceEntity,
					rpt_ctx->saHpiResourceEntityPath, 
					SAHPI_MAX_TEXT_BUFFER_LENGTH );

		/* saHpiResourceCapabilities */
		SaHpiTextBufferT res_cap_buf;
		if (SA_OK == oh_decode_capability(
					rpt_entry.ResourceCapabilities,
					&res_cap_buf)) {
			memcpy(rpt_ctx->saHpiResourceEntityPath,
			       res_cap_buf.Data,
			       res_cap_buf.DataLength);
			rpt_ctx->saHpiResourceEntityPath_len = 
				res_cap_buf.DataLength;
		} else {
			rc = -1;
			dbg("ERROR: saHpiResourceCapabilities, populate_rpt");
			break;
		}

		/* saHpiResourceHotSwapCapabilities */
		rpt_ctx->saHpiResourceHotSwapCapabilities =
			rpt_entry.HotSwapCapabilities;

		/* saHpiResourceSeverity */
		rpt_ctx->saHpiResourceSeverity = 
			rpt_entry.ResourceSeverity;

		/* saHpiResourceFailed */
		rpt_ctx->saHpiResourceFailed = 
			( rpt_entry.ResourceFailed == 0 ) ? 1 : 2 ;

		/* saHpiResourceInfoResourceRev */
		rpt_ctx->saHpiResourceInfoResourceRev =
			rpt_entry.ResourceInfo.ResourceRev;

		/* saHpiResourceInfoSpecificVer */
		rpt_ctx->saHpiResourceInfoSpecificVer = 
			rpt_entry.ResourceInfo.SpecificVer;

		/* saHpiResourceInfoDeviceSupport */
		rpt_ctx->saHpiResourceInfoDeviceSupport = 
			rpt_entry.ResourceInfo.DeviceSupport;

		/* saHpiResourceInfoManufacturerId */
		rpt_ctx->saHpiResourceInfoManufacturerId = 
			rpt_entry.ResourceInfo.ManufacturerId;

		/* saHpiResourceInfoProductId */
		rpt_ctx->saHpiResourceInfoProductId =
			rpt_entry.ResourceInfo.ProductId;

		/* saHpiResourceInfoFirmwareMajorRev */
		rpt_ctx->saHpiResourceInfoFirmwareMajorRev = 
			rpt_entry.ResourceInfo.FirmwareMajorRev;

		/* saHpiResourceInfoFirmwareMinorRev */
		rpt_ctx->saHpiResourceInfoFirmwareMinorRev =
			rpt_entry.ResourceInfo.FirmwareMinorRev;

		/* saHpiResourceInfoAuxFirmwareRev */
		rpt_ctx->saHpiResourceInfoAuxFirmwareRev =
			rpt_entry.ResourceInfo.AuxFirmwareRev;

		/* saHpiResourceInfoGuid */
		memcpy(rpt_ctx->saHpiResourceInfoGuid,
		       rpt_entry.ResourceInfo.Guid,
		       16);
		rpt_ctx->saHpiResourceInfoGuid_len = 16;

		/* saHpiResourceTag */
		rpt_ctx->saHpiResourceTagTextType = 
			rpt_entry.ResourceTag.DataType+1;

		rpt_ctx->saHpiResourceTagTextLanguage = 
			rpt_entry.ResourceTag.Language+1;

		memcpy(rpt_ctx->saHpiResourceTag,
		       rpt_entry.ResourceTag.Data,
		       rpt_entry.ResourceTag.DataLength);

		rpt_ctx->saHpiResourceTag_len = 
			rpt_entry.ResourceTag.DataLength;

		/* saHpiResourceParamControl */
		/* write only access provided by asHpiParmControl() */
		
		/* commit/add */
		CONTAINER_INSERT(cb.container, rpt_ctx);

		populate_hot_swap(&rpt_entry);

	}

	DEBUGMSGTL ((AGENT, "populate_rpt. Exit (rc: %d).\n", rc));

	return rc;

}



