/************************************************************
 * the *_row_copy routine
 */
static int saHpiSoftwareEventTable_row_copy(saHpiSoftwareEventTable_context * dst,
                         saHpiSoftwareEventTable_context * src)
{
    if(!dst||!src)
        return 1;
        
    /*
     * copy index, if provided
     */
    if(dst->index.oids)
        free(dst->index.oids);
    if(snmp_clone_mem( (void*)&dst->index.oids, src->index.oids,
                           src->index.len * sizeof(oid) )) {
        dst->index.oids = NULL;
        return 1;
    }
    dst->index.len = src->index.len;
    

    /*
     * copy components into the context structure
     */
    /** TODO: add code for external index(s)! */
    dst->saHpiSoftwareEventEntryId = src->saHpiSoftwareEventEntryId;

    dst->saHpiSoftwareEventTimestamp = src->saHpiSoftwareEventTimestamp;

    dst->saHpiSoftwareEventManufacturerIdT = src->saHpiSoftwareEventManufacturerIdT;

    dst->saHpiSoftwareEventType = src->saHpiSoftwareEventType;

    dst->saHpiSoftwareEventTextType = src->saHpiSoftwareEventTextType;

    dst->saHpiSoftwareEventTextLanguage = src->saHpiSoftwareEventTextLanguage;

    memcpy( dst->saHpiSoftwareEventText, src->saHpiSoftwareEventText, src->saHpiSoftwareEventText_len );
    dst->saHpiSoftwareEventText_len = src->saHpiSoftwareEventText_len;

    return 0;
}

