static netsnmp_handler_registration *my_handler = NULL;
static netsnmp_table_array_callbacks cb;

static oid saHpiWatchdogTable_oid[] = { saHpiWatchdogTable_TABLE_OID };
static size_t saHpiWatchdogTable_oid_len =
OID_LENGTH (saHpiWatchdogTable_oid);

//{ 1, 3, 6, 1, 3, 90, 3, 9, 0 }; 
static oid saHpiWatchdogCount_oid[] = { hpiResources_OID, 9, 0 };
static u_long watchdog_count;

static int
saHpiWatchdogTable_modify_context (SaHpiWatchdogRecT * entry,
				   SaHpiRptEntryT * rpt_entry,
				   SaHpiWatchdogT * wdog,
				   oid *, size_t,
				   saHpiWatchdogTable_context * ctx);




int
populate_watchdog (SaHpiWatchdogRecT * watchdog,
		   SaHpiRptEntryT * rpt_entry,
		   oid * rdr_entry_oid, size_t rdr_entry_oid_len,
		   oid * watchdog_oid, size_t * watchdog_oid_len)
{

  SaHpiWatchdogT wdog;
  SaHpiSessionIdT session_id;
  int rc = AGENT_ERR_NOERROR;

  oid index_oid[WATCHDOG_INDEX_NR];
  oid column[2];

  netsnmp_index watchdog_index;
  saHpiWatchdogTable_context *watchdog_context;

  DEBUGMSGTL ((AGENT, "\n\t--- populate_watchdog: Entry.\n"));

  if (watchdog)
    {

      rc = AGENT_ERR_NOERROR;
      watchdog_index.len = WATCHDOG_INDEX_NR;

      // Look at the MIB to find out what the indexs are
      index_oid[0] = rpt_entry->DomainId;
      index_oid[1] = rpt_entry->ResourceId;
      index_oid[2] = watchdog->WatchdogNum;
      // Possible more indexes?

      watchdog_index.oids = (oid *) & index_oid;
      // We are re-populating. Check for existing entries
      watchdog_context = NULL;
      watchdog_context = CONTAINER_FIND (cb.container, &watchdog_index);
      // If we don't find it - we create it.
      if (!watchdog_context)
	{
	  // New entry. Add it
	  watchdog_context = saHpiWatchdogTable_create_row (&watchdog_index);
	}
      if (!watchdog_context)
	{
	  snmp_log (LOG_ERR, "Not enough memory for a watchdog row!");
	  return AGENT_ERR_INTERNAL_ERROR;
	}
      // Generate our full OID
      column[0] = 1;
      column[1] = COLUMN_SAHPIWATCHDOGNUM;

      build_full_oid (saHpiWatchdogTable_oid, saHpiWatchdogTable_oid_len,
		      column, 2,
		      &watchdog_index,
		      watchdog_oid, MAX_OID_LEN, watchdog_oid_len);


      // By this stage, watchdog_context surely has something in it.
      // '*_modify_context' does a checksum check to see if 
      // the record needs to be altered, and if so populates with
      // information from RDR and the OIDs passed.
      rc = getSaHpiSession (&session_id);
      if (rc != AGENT_ERR_NOERROR)
	{
	  DEBUGMSGTL ((AGENT, "Call to getSaHpiSession failed with rc: %d\n",
		       rc));
	  return rc;
	}

      DEBUGMSGTL ((AGENT, "Calling saHpiWatchdogTimerGet with %d\n",
		   watchdog->WatchdogNum));

      rc = saHpiWatchdogTimerGet (session_id, rpt_entry->ResourceId,
				  watchdog->WatchdogNum, &wdog);


      if (rc != SA_OK)
	{
	  snmp_log (LOG_ERR,
		    "Call to saHpiWatchdogTimerGet failed with return code: %s.\n",
		    get_error_string (rc));
	  DEBUGMSGTL ((AGENT,
		       "Call to saHpiWatchdogTimerGet failed with rc: %s\n",
		       get_error_string (rc)));
	  return AGENT_ERR_OPERATION;
	}

      if (saHpiWatchdogTable_modify_context (watchdog, rpt_entry, &wdog,
					     rdr_entry_oid, rdr_entry_oid_len,
					     watchdog_context)
	  == AGENT_NEW_ENTRY)
	{

	  CONTAINER_INSERT (cb.container, watchdog_context);
	  watchdog_count = CONTAINER_SIZE (cb.container);

	}
      rc = AGENT_ERR_NOERROR;
    }
  else
    rc = AGENT_ERR_OPERATION;

  DEBUGMSGTL ((AGENT, "\n\t--- populate_watchdog. Exit\n"));
  return rc;
}


int
delete_watchdog_row (SaHpiDomainIdT domain_id,
		     SaHpiResourceIdT resource_id, SaHpiWatchdogNumT num)
{

  saHpiWatchdogTable_context *ctx;
  oid index_oid[WATCHDOG_INDEX_NR];
  netsnmp_index index;
  int rc = AGENT_ERR_NOT_FOUND;

  DEBUGMSGTL ((AGENT, "delete_watchdog_row (%d, %d, %d). Entry.\n",
	       domain_id, resource_id, num));

  // Look at the MIB to find out what the indexs are
  index_oid[0] = domain_id;
  index_oid[1] = resource_id;
  index_oid[2] = num;
  // Possible more indexes?
  index.oids = (oid *) & index_oid;
  index.len = WATCHDOG_INDEX_NR;
  ctx = CONTAINER_FIND (cb.container, &index);

  if (ctx)
    {
      CONTAINER_REMOVE (cb.container, ctx);
      saHpiWatchdogTable_delete_row (ctx);
      watchdog_count = CONTAINER_SIZE (cb.container);
      rc = AGENT_ERR_NOERROR;
    }
  DEBUGMSGTL ((AGENT, "delete_watchdog_row. Exit (rc: %d).\n", rc));
  return rc;
}

int
saHpiWatchdogTable_modify_context (SaHpiWatchdogRecT * entry,
				   SaHpiRptEntryT * rpt_entry,
				   SaHpiWatchdogT * wdog,
				   oid * rdr_entry, size_t rdr_entry_oid_len,
				   saHpiWatchdogTable_context * ctx)
{
  unsigned int update_entry = MIB_FALSE;
  long hash;

  // Make sure they are valid.
  if (entry && ctx)
    {

      // We are subtracting SaHpiTextBufferT b/c the underlaying HPI
      // library is not zeroing out the memory for not used entries -
      // thus garbage in SaHpiTextBufferT exist,
      hash = calculate_hash_value (entry, sizeof (SaHpiWatchdogRecT));

      DEBUGMSGTL ((AGENT, " Hash value: %d, in ctx: %d\n", hash, ctx->hash));

      if (ctx->hash != 0)
	{
	  // Only do the check if the hash value is something else than zero.
	  // 'zero' value is only for newly created records, and in some
	  // rare instances when the hash has rolled to zero - in which
	  // case we will just consider the worst-case scenario and update
	  // the record and not trust the hash value.
	  if (hash == ctx->hash)
	    {
	      // The same data. No need to change.
	      return AGENT_ENTRY_EXIST;
	    }
	  if ((ctx->resource_id == rpt_entry->ResourceId) &&
	      (ctx->domain_id == rpt_entry->DomainId) &&
	      (ctx->saHpiWatchdogNum == entry->WatchdogNum)) {
		  update_entry = MIB_TRUE;
		  DEBUGMSGTL((AGENT,"Updating HotSwap entry. [%d, %d, %d]\n",
					  rpt_entry->DomainId,
					  rpt_entry->ResourceId,
					  entry->WatchdogNum));
	     }
	}

      if (hash == 0)
	hash = 1;
      ctx->hash = hash;

      ctx->saHpiWatchdogRDR_len = rdr_entry_oid_len * sizeof (oid);
      memcpy (ctx->saHpiWatchdogRDR, rdr_entry, ctx->saHpiWatchdogRDR_len);
      ctx->resource_id = rpt_entry->ResourceId;
      ctx->domain_id = rpt_entry->DomainId;
      ctx->saHpiWatchdogNum = entry->WatchdogNum;
      ctx->saHpiWatchdogOem = entry->Oem;
      ctx->saHpiWatchdogTimerReset = MIB_FALSE;
      if (wdog)
	{
	  ctx->saHpiWatchdogLog =
	    (wdog->Log == SAHPI_TRUE) ? MIB_TRUE : MIB_FALSE;
	  ctx->saHpiWatchdogRunning =
	    (wdog->Running == SAHPI_TRUE) ? MIB_TRUE : MIB_FALSE;
	  ctx->saHpiWatchdogTimerUse = wdog->TimerUse + 1;
	  ctx->saHpiWatchdogTimerAction = wdog->TimerAction + 1;
	  ctx->saHpiWatchdogPretimerInterrupt = wdog->PretimerInterrupt + 1;
	  ctx->saHpiWatchdogPreTimeoutInterval = wdog->PreTimeoutInterval;
	  ctx->saHpiWatchdogTimerUseExpFlags = wdog->TimerUseExpFlags;
	  ctx->saHpiWatchdogTimerInitialCount = wdog->InitialCount;
	  ctx->saHpiWatchdogTimerPresentCount = wdog->PresentCount;
	}
      if (update_entry == MIB_TRUE)
	      return AGENT_ENTRY_EXIST;
      return AGENT_NEW_ENTRY;
    }

  return AGENT_ERR_NULL_DATA;
}

int
set_timer_reset (saHpiWatchdogTable_context * ctx)
{
  SaHpiWatchdogT wdog;
  SaHpiSessionIdT session_id;
  SaErrorT rc;

  if (ctx)
    {
      if (ctx->saHpiWatchdogRunning == MIB_TRUE)
	{

	  // Get the seesion_id
	  rc = getSaHpiSession (&session_id);
	  if (rc != AGENT_ERR_NOERROR)
	    {
	      DEBUGMSGTL ((AGENT,
			   "Call to getSaHpiSession failed with rc: %d\n",
			   rc));
	      return rc;
	    }

	  DEBUGMSGTL ((AGENT, "Calling saHpiWatchdogTimerReset with %d\n",
		       ctx->saHpiWatchdogNum));

	  rc = saHpiWatchdogTimerReset (session_id,
					ctx->resource_id,
					ctx->saHpiWatchdogNum);

	  if (rc != SA_OK)
	    {
	      snmp_log (LOG_ERR,
			"Call to saHpiWatchdogTimerReset failed with rc: %s\n",
			get_error_string (rc));
	      DEBUGMSGTL ((AGENT,
			   "Call to saHpiWatchdogTimerReset failed with rc: %s\n",
			   get_error_string (rc)));
	      return AGENT_ERR_OPERATION;
	    }
	  // Get the new changes.
	  DEBUGMSGTL ((AGENT, "Calling saHpiWatchdogTimerGet with %d\n",
		       ctx->saHpiWatchdogNum));
	  memset (&wdog, 0x00, sizeof (SaHpiWatchdogT));

	  rc = saHpiWatchdogTimerGet (session_id, ctx->resource_id,
				      ctx->saHpiWatchdogNum, &wdog);
	  if (rc != SA_OK)
	    {
	      snmp_log (LOG_ERR,
			"Call to saHpiWatchdogTimerGet failed with rc: %s.\n",
			get_error_string (rc));
	      DEBUGMSGTL ((AGENT,
			   "Call to saHpiWatchdogTimerGet failed with rc: %s.\n",
			   get_error_string (rc)));
	      return AGENT_ERR_OPERATION;
	    }
	  DEBUGMSGTL ((AGENT, "log: %d, Run: %d\n", wdog.Log, wdog.Running));
	  ctx->saHpiWatchdogLog =
	    (wdog.Log == SAHPI_TRUE) ? MIB_TRUE : MIB_FALSE;
	  ctx->saHpiWatchdogRunning =
	    (wdog.Running == SAHPI_TRUE) ? MIB_TRUE : MIB_FALSE;
	  ctx->saHpiWatchdogTimerUse = wdog.TimerUse + 1;
	  ctx->saHpiWatchdogTimerAction = wdog.TimerAction + 1;
	  ctx->saHpiWatchdogPretimerInterrupt = wdog.PretimerInterrupt + 1;
	  ctx->saHpiWatchdogPreTimeoutInterval = wdog.PreTimeoutInterval;
	  ctx->saHpiWatchdogTimerUseExpFlags = wdog.TimerUseExpFlags;
	  ctx->saHpiWatchdogTimerInitialCount = wdog.InitialCount;
	  ctx->saHpiWatchdogTimerPresentCount = wdog.PresentCount;
	  // MIB change. 
	  // IBM-KR: Should this be set to something else, perhaps?
	  ctx->saHpiWatchdogTimerReset = MIB_FALSE;
	  return AGENT_ERR_NOERROR;
	}
      return AGENT_ERR_OPERATION;
    }
  return AGENT_ERR_NULL_DATA;

}

int
set_watchdog (saHpiWatchdogTable_context * ctx)
{

  SaHpiWatchdogT wdog;
  SaHpiSessionIdT session_id;
  SaErrorT rc;

  if (ctx)
    {
      wdog.Log =
	(ctx->saHpiWatchdogLog == MIB_TRUE) ? SAHPI_TRUE : SAHPI_FALSE;
      wdog.Running =
	(ctx->saHpiWatchdogRunning == MIB_TRUE) ? SAHPI_TRUE : SAHPI_FALSE;
      wdog.TimerUse = ctx->saHpiWatchdogTimerUse + 1;
      wdog.TimerAction = ctx->saHpiWatchdogTimerAction + 1;
      wdog.PretimerInterrupt = ctx->saHpiWatchdogPretimerInterrupt + 1;
      wdog.PreTimeoutInterval = ctx->saHpiWatchdogPreTimeoutInterval;
      wdog.TimerUseExpFlags = ctx->saHpiWatchdogTimerUseExpFlags;
      wdog.InitialCount = ctx->saHpiWatchdogTimerInitialCount;
      wdog.PresentCount = ctx->saHpiWatchdogTimerPresentCount;

      // Get the seesion_id
      rc = getSaHpiSession (&session_id);
      if (rc != AGENT_ERR_NOERROR)
	{
	  DEBUGMSGTL ((AGENT, "Call to getSaHpiSession failed with rc: %d\n",
		       rc));
	  return rc;
	}
      DEBUGMSGTL ((AGENT, "Calling saHpiWatchdogTimerSet with %d\n",
		   ctx->saHpiWatchdogNum));
      rc =
	saHpiWatchdogTimerSet (session_id, ctx->resource_id,
			       ctx->saHpiWatchdogNum, &wdog);


      if (rc != SA_OK)
	{
	  snmp_log (LOG_ERR,
		    "Call to saHpiWatchdogTimerSet failed with return code: %s\n",
		    get_error_string (rc));
	  DEBUGMSGTL ((AGENT,
		       "Call saHpiWatchdogTimerSet failed with return code: %s\n",
		       get_error_string (rc)));
	  return AGENT_ERR_OPERATION;
	}

      DEBUGMSGTL ((AGENT, "Calling saHpiWatchdogTimerGet with %d\n",
		   ctx->saHpiWatchdogNum));

      rc = saHpiWatchdogTimerGet (session_id, ctx->resource_id,
				  ctx->saHpiWatchdogNum, &wdog);


      if (rc != SA_OK)
	{
	  snmp_log (LOG_ERR,
		    "Call to saHpiWatchdogTimerGet failed with rc: %s\n",
		    get_error_string (rc));
	  DEBUGMSGTL ((AGENT,
		       "Call to saHpiWatchdogTimerGet failed with rc: %s\n",
		       get_error_string (rc)));
	  return AGENT_ERR_OPERATION;
	}

      ctx->saHpiWatchdogLog = (wdog.Log == SAHPI_TRUE) ? MIB_TRUE : MIB_FALSE;
      ctx->saHpiWatchdogRunning =
	(wdog.Running == SAHPI_TRUE) ? MIB_TRUE : MIB_FALSE;
      ctx->saHpiWatchdogTimerUse = wdog.TimerUse + 1;
      ctx->saHpiWatchdogTimerAction = wdog.TimerAction + 1;
      ctx->saHpiWatchdogPretimerInterrupt = wdog.PretimerInterrupt + 1;
      ctx->saHpiWatchdogPreTimeoutInterval = wdog.PreTimeoutInterval;
      ctx->saHpiWatchdogTimerUseExpFlags = wdog.TimerUseExpFlags;
      ctx->saHpiWatchdogTimerInitialCount = wdog.InitialCount;
      ctx->saHpiWatchdogTimerPresentCount = wdog.PresentCount;
      return AGENT_ERR_NOERROR;
    }
  return AGENT_ERR_NULL_DATA;
}

int
update_watchdog_row (SaHpiDomainIdT domain_id,
		     SaHpiResourceIdT resource_id,
		     SaHpiWatchdogNumT num, SaHpiWatchdogEventT * wdog)
{
  saHpiWatchdogTable_context *ctx;
  oid index_oid[WATCHDOG_INDEX_NR];
  netsnmp_index index;

  // Look at the MIB to find out what the indexs are
  index_oid[0] = domain_id;
  index_oid[1] = resource_id;
  index_oid[2] = num;
  // Possible more indexes?
  index.oids = (oid *) & index_oid;
  index.len = WATCHDOG_INDEX_NR;
  ctx = CONTAINER_FIND (cb.container, &index);

  if (ctx)
    {
      ctx->saHpiWatchdogTimerUse = wdog->WatchdogUse + 1;
      ctx->saHpiWatchdogTimerAction = wdog->WatchdogAction + 1;
      ctx->saHpiWatchdogPretimerInterrupt = wdog->WatchdogPreTimerAction + 1;
      return AGENT_ERR_NOERROR;
    }

  return AGENT_ERR_NOT_FOUND;

}

/************************************************************
 * the *_row_copy routine
 */

static int
saHpiWatchdogTable_row_copy (saHpiWatchdogTable_context * dst,
			     saHpiWatchdogTable_context * src)
{
  if (!dst || !src)
    return 1;

  /*
   * copy index, if provided
   */
  if (dst->index.oids)
    free (dst->index.oids);
  if (snmp_clone_mem ((void *) &dst->index.oids, src->index.oids,
		      src->index.len * sizeof (oid)))
    {
      dst->index.oids = NULL;
      return 1;
    }
  dst->index.len = src->index.len;


  dst->saHpiWatchdogNum = src->saHpiWatchdogNum;

  dst->saHpiWatchdogLog = src->saHpiWatchdogLog;

  dst->saHpiWatchdogRunning = src->saHpiWatchdogRunning;

  dst->saHpiWatchdogTimerUse = src->saHpiWatchdogTimerUse;

  dst->saHpiWatchdogTimerAction = src->saHpiWatchdogTimerAction;

  dst->saHpiWatchdogPretimerInterrupt = src->saHpiWatchdogPretimerInterrupt;

  dst->saHpiWatchdogPreTimeoutInterval = src->saHpiWatchdogPreTimeoutInterval;

  dst->saHpiWatchdogTimerUseExpFlags = src->saHpiWatchdogTimerUseExpFlags;

  dst->saHpiWatchdogTimerInitialCount = src->saHpiWatchdogTimerInitialCount;

  dst->saHpiWatchdogTimerPresentCount = src->saHpiWatchdogTimerPresentCount;

  dst->saHpiWatchdogOem = src->saHpiWatchdogOem;

  dst->saHpiWatchdogRDR_len = src->saHpiWatchdogRDR_len;
  memcpy (dst->saHpiWatchdogRDR, src->saHpiWatchdogRDR,
	  src->saHpiWatchdogRDR_len);

  dst->hash = src->hash;
  dst->resource_id = src->resource_id;
  dst->saHpiWatchdogTimerReset = src->saHpiWatchdogTimerReset;
  return 0;
}



/*
 * the *_extract_index routine
 */
int
saHpiWatchdogTable_extract_index (saHpiWatchdogTable_context * ctx,
				  netsnmp_index * hdr)
{
  /*
   * temporary local storage for extracting oid index
   */
  netsnmp_variable_list var_saHpiDomainID;
  netsnmp_variable_list var_saHpiResourceID;
  netsnmp_variable_list var_saHpiWatchdogNum;
  int err;

  /*
   * copy index, if provided
   */
  if (hdr)
    {
      netsnmp_assert (ctx->index.oids == NULL);
      if (snmp_clone_mem ((void *) &ctx->index.oids, hdr->oids,
			  hdr->len * sizeof (oid)))
	{
	  return -1;
	}
      ctx->index.len = hdr->len;
    }

    /**
     * Create variable to hold each component of the index
     */

  memset (&var_saHpiDomainID, 0x00, sizeof (var_saHpiDomainID));
  var_saHpiDomainID.type = ASN_UNSIGNED;
  var_saHpiDomainID.next_variable = &var_saHpiResourceID;

  memset (&var_saHpiResourceID, 0x00, sizeof (var_saHpiResourceID));
  var_saHpiResourceID.type = ASN_UNSIGNED;
  var_saHpiResourceID.next_variable = &var_saHpiWatchdogNum;

  memset (&var_saHpiWatchdogNum, 0x00, sizeof (var_saHpiWatchdogNum));
  var_saHpiWatchdogNum.type = ASN_UNSIGNED;
  var_saHpiWatchdogNum.next_variable = NULL;

  /*
   * parse the oid into the individual components
   */
  err = parse_oid_indexes (hdr->oids, hdr->len, &var_saHpiDomainID);
  if (err == SNMP_ERR_NOERROR)
    {
      /*
       * copy components into the context structure
       */
      ctx->saHpiWatchdogNum = *var_saHpiWatchdogNum.val.integer;

    }

  /*
   * parsing may have allocated memory. free it.
   */
  snmp_reset_var_buffers (&var_saHpiDomainID);

  return err;
}

