	  rc = netsnmp_check_vb_type_and_size (var, ASN_INTEGER,
					       sizeof (row_ctx->
						       saHpiWatchdogTimerUse));
	  break;

	case COLUMN_SAHPIWATCHDOGTIMERACTION:
