  cb.set_free = saHpiWatchdogTable_set_free;
  cb.set_undo = saHpiWatchdogTable_set_undo;

  DEBUGMSGTL (("initialize_table_saHpiWatchdogTable",
	       "Registering table saHpiWatchdogTable " "as a table array\n"));
